cipher_1 = input()
clear_1 = input()
cipher_2 = input()
clear_3 = input()

alphanum="0123456789ABCDEFGHIJKLMNOPQRSTUVWXYZ $%*+-./:"
numalpha={c:alphanum.index(c) for c in alphanum}
div=[i for i in range(2,len(cipher_1)) if len(cipher_1)%i==0]

# Les éléments inversibles
inv45={i for i in range(45) if i%3 and i%5}
# Les inverses des éléments inversibles
inv45b=[[None,pow(i,23,45)][i in inv45] for i in range(45)]

def combinations(liste,l):
  n=len(liste)
  for i in range(2**n):
    b=list(map(int,bin(i)[2:].rjust(n,"0")))
    if sum(b)==l:
      yield [liste[j] for j in range(n) if b[j]]

def prodmat(M,V):
    """Calcule le produit d’une matrice et d’un vecteur colonne."""
    return [sum(l[i]*V[i] for i in range(len(V)))%45 for l in M]

def chiffre(H,m):
    """Chiffre le vecteur m en utilisant la matrice de Hill H.
    S’utilise aussi pour déchiffrer en utilisant l’inverse de H."""
    if len(m)%len(H):
        raise ValueError("La longueur de la clé doit diviser la longueur du message.")
    if det(H)!=1:
        raise ValueError("Le déterminant de la clé doit être égal à 1, il est %d."%det(H))
    h=len(H)
    c=""
    for i in range(len(m)//h):
        V=[numalpha[c] for c in m[h*i:h*(i+1)]]
        c+="".join(alphanum[c] for c in prodmat(H,V))
    return c

def det(M):
    """Calcule le déterminant d’une matrice récursivement en développant selon la première colonne.
    Noter qu’une matrice de Hill a un déterminant égal à 1 en principe."""
    if any(len(M)!=len(l) for l in M):
        raise ValueError("La matrice n’est pas carrée")
    if len(M)==1:
        return M[0][0]
    s=0
    for i,l in enumerate(M):
        if M[i][0]:
            N=[l[1:] for j,l in enumerate(M) if j!=i]
            s+=(-1)**i*M[i][0]*det(N)
    return s%45

def pgcd(a,b):
    """Calcule le PGCD de deux nombres."""
    a,b=abs(a),abs(b)
    a,b=max(a,b),min(a,b)
    while a:
        b,a=a,b%a
    return b

def gcd(l):
    """Calcule le PGCD de plusieurs nombres récursivement."""
    if len(l)==1:
        return l[0]
    return pgcd(l[0],gcd(l[1:]))

def gauss(mat):
    """Algorithme du pivot de Gauß pour créer une matrice triangulaire supérieure.
    La technique est récursive.
    L’algorithme est légèrement adapté pour notre cas modulaire."""
    m=list(mat)
    for i in range(len(m)):
        if m[i][0]<0:
            for j in range(len(m[0])):
                m[i][j]*=-1
        g=gcd(m[i])
        if g>1:
            for j in range(len(m[0])):
                m[i][j]//=g
    if len(mat)==1:
        return mat
    while True:
        c=[l[0] for l in m]
        pivot=c.index(min(set(c)-{0}))
        m[pivot],m[0]=m[0],m[pivot]
        if c.count(0)==len(m)-1:
            break
        for i in range(1,len(m)):
            if m[i][0]:
                for j in range(len(m[0])):
                    m[i][j]-=m[0][j]
                    m[i][j]%=45
    m1=gauss([l[1:] for l in m[1:]])
    return [m[0]]+[[0]+l for l in m1]

def diagonale(mat):
    """Fin de l’algorithme du pivot de Gauß, on se débrouille pour que la diagonale soit unitaire."""
    m=list(mat)
    for i in range(len(m)):
        b=inv45b[m[i][i]]
        for j in range(i,len(m[0])):
            m[i][j]*=b
            m[i][j]%=45
    for i in range(len(m)-1,0,-1):
        for j in range(i-1,-1,-1):
            for k in range(len(m),len(m[0])):
                m[j][k]-=m[j][i]*m[i][k]
                m[j][k]%=45
            m[j][i]=0
    return [l[len(m):] for l in m]

def plus1(mat):
    """En vue de l’inversion de matrice, on concatène la matrice unité à droite."""
    n=len(mat)
    return [l+[0]*i+[1]+[0]*(n-i-1) for i,l in enumerate(mat)]

def invmat(mat):
    """Calcul de la matrice modulaire inverse."""
    return diagonale(gauss(plus1(mat)))

def système(mat,v):
    """Résout le système de matrice m et de vecteur v."""
    return [e[0] for e in diagonale(gauss([l+[v[i]] for i,l in enumerate(mat)]))]

for l in div:
    """On cherche les l-uplets qui rendent la recherche de la matrice de Hill inversible."""
    mat=[[0]*l**2 for _ in range(l**2)]
    c=[]
    for c in combinations(range(len(clear_1)//l),l):
        for i in range(l):
            for j in range(l):
                mat[i*l+j][j*l:j*l+l]=[numalpha[C] for C in clear_1[c[i]*l:c[i]*l+l]]
        if pgcd(det([L[:l] for L in mat[::l]]),45)==1:
            break
    v=[numalpha[c] for c in "".join(cipher_1[i*l:i*l+l] for i in c)[:l**2]]
    H=[]
    m=système(mat,v)
    for i in range(l):
        H.append(m[i*l:i*l+l])
    """Si la matrice de Hill est correcte, les deux premiers messages sont inverses l’un de l’autre."""
    if det(H)==1 and chiffre(H,clear_1)==cipher_1:
        Hi=invmat(H)
        print(chiffre(Hi,cipher_2))
        print(chiffre(H,clear_3))
        break
