import java.util.*;
import java.util.regex.Matcher;
import java.util.regex.Pattern;

class Solution {

    public static void main(String args[]) {
        Scanner in = new Scanner(System.in);
        int N = in.nextInt();
        if (in.hasNextLine()) {
            in.nextLine();
        }
        StringBuilder sb = new StringBuilder();
        for (int i = 0; i < N; i++) {
            sb.append(in.nextLine());
        }
        String t = sb.toString();
        Matcher con = Pattern.compile("[[a-z&&[^aeiouy]][A-Z&&[^AEIOUY]]]").matcher(t);
        Matcher voy = Pattern.compile("[aeiouyAEIOUY]").matcher(t);
        int i = 0,j=0;
        while(con.find()){i++;}
        while(voy.find()){j++;}
        System.out.println(i*j);
    }
}
