import math
a, b, c =[int(i) for i in input().split()]
d = int(input())
ans = []
a, b, c = str(a), str(b), str(c)
for i in '+-*/':
    for j in '+-*/':
        try:
            check = int(eval(a + i + b + j + c))
            if isinstance(check, float):
                check = int(check)
            if check == d:
                ans.append(a + i + b + j + c)
        except ZeroDivisionError:
            pass
if len(ans) > 1:
    print(len(ans))
elif len(ans) == 1:
    print(*ans)
else:
    print(-1)
