var out =[];
var range = [];
var i =0;
var n;
while(i< sorted.length){
  n=sorted[i];
  if(range.length == 0){
    range.push(n);
  }else{
    //range already exists. is it a continuation of the range or is it a range breaker
    if(n-1 == range[range.length-1]){
      //its a continuation
      range.push(n);
    }else{
      //its not a continuation of range. lets clear range out and start a new one
      //first check to see if range > 1
      if (range.length > 1){
        //there are 2 items
        out.push(range[0] + "-" + range[range.length-1]);
      }else{
        //there is 1 item
        out.push(range[0]);
      }
      //clear it and push the new number
      range=[];
      range.push(n);     
    }
  }  
  i++;
}
