s = input()
n = int(input())
m = ''
ans = {}

for _ in range(n):
    m += ''.join([i for i in input() if i.isalpha()])

if len(m) - len(s) >= 1:
    m, s = [*m], [*s]
    while s:
        target = s.pop(0)
        m.pop(m.index(target))
    print('Incorrect', ''.join(sorted(m)))
else:
    print('Correct')
