const length = parseInt(readline());
const keyPhrase = readline();
const l = parseInt(readline());
let tot = 0;
for (let i = 0; i < l; i++) {
    const titleAndLength = readline();
    let [title, duration] = titleAndLength.split`, `.map(r=>r.toLowerCase())
    if (!title.includes(keyPhrase.toLocaleLowerCase())){
        tot += +duration
    }
}

console.log(tot/60 >= length?`Long Enough`:length*60-tot)
