T = readline()
H = +readline()
M = +readline()

tH = +T.split(':')[0]
tM = +T.split(':')[1]

min = 6 * tM;//Every minute the minute hand moves 6 degrees, 6*60 = 360
hourPerMin = 0.5 * tM;//Every minute the hour hand moves 0.5 degrees
hourPerHour = 30 * tH;//Every hour the hour hand moves 30 degrees

handsAngle = Math.abs(hourPerHour + hourPerMin - min);
hourAngle = Math.abs((hourPerHour + hourPerMin) - (30 * H));
minAngle = Math.abs(min - (6 * M));

//Requirements say to print the smallest angle
console.log(handsAngle > 180 ? 360 - handsAngle : handsAngle);
console.log(hourAngle > 180 ? 360 - hourAngle : Math.abs(hourAngle));
console.log(minAngle > 180 ? 360 - minAngle : Math.abs(minAngle));
