const N = parseInt(readline())

let range = Array.from({length: N-1}, (v,i) => i+2)
let idx = 0

while(idx < range.length) {
    const currentPrime = range[idx]
    range = range.filter(v => v <= currentPrime || v % currentPrime !== 0)
    idx += 1 // idx is consistent for range array since the first values are not filtered out
}

