import sys
import math

# Auto-generated code below aims at helping you parse
# the standard input according to the problem statement.

password = input()
n = int(input())
conditions = []
for i in range(n):
    cond = input().split(" ")
    conditions.append((cond[0], cond[1]))

#checking if conditions contradict each other
inc = []
exc = []
minL = -1
maxL = -1
condError = False
for c in conditions:
    if c[0] == "lengthMax":
        if maxL == -1:
            maxL = int(c[1])
        else:
            maxL = min(maxL, int(c[1]) )
    elif c[0] == "lengthMin":
        minL = max(minL, int(c[1]))
    if c[0] == "include":
        inc.append(c[1])      
    elif c[0] == "exclude":
        for i in c[1]:

            exc.append(i)  
if minL > maxL and maxL != -1:
    condError = True
else:
    for lst in inc:
        # if all of them should be excluded there is an error
        allExc = True
        for l in lst:
            if l not in exc:
                allExc = False
        if allExc:
            condError = True
            break

#checking validity
valid = True
for c in conditions:
    if c[0] == "lengthMax" and len(password) > int(c[1]):
        valid = False
    elif c[0] == "lengthMin" and len(password) < int(c[1]):
        valid = False
    if c[0] == "include":
        valid = False
        for l in c[1]:
            if l in password:
                valid = True
                break        
    elif c[0] == "exclude":
        for l in c[1]:
            if l in password:
                valid = False
                break  
    if not valid:
        break

if condError:
    print("ERROR")
elif valid:
    print("VALID")
else:
    print("INVALID")
