def pgcd(a,b):
    a,b=abs(a),abs(b)
    a,b=max(a,b),min(a,b)
    while a:
        b,a=a,b%a
    return b

def gcd(l):
    if len(l)==1:
        return l[0]
    return pgcd(l[0],gcd(l[1:]))

def gauss(mat):
    m=list(mat)
    for i in range(len(m)):
        if m[i][0]<0:
            for j in range(len(m[0])):
                m[i][j]*=-1
        g=gcd(m[i])
        if g>1:
            for j in range(len(m[0])):
                m[i][j]//=g
    if len(mat)==1:
        return mat
    while True:
        c=[l[0] for l in m]
        pivot=c.index(min(set(c)-{0}))
        m[pivot],m[0]=m[0],m[pivot]
        if c.count(0)==len(m)-1:
            break
        for i in range(1,len(m)):
            if m[i][0]:
                for j in range(len(m[0])):
                    m[i][j]-=m[0][j]
    m1=gauss([l[1:] for l in m[1:]])
    return [m[0]]+[[0]+l for l in m1]

def diagonale(mat):
    m=list(mat)
    n=len(m[0])-1
    for i in range(len(m)-1,0,-1):
        for j in range(i-1,-1,-1):
            m[j][-1]-=m[j][i]*m[i][-1]
            m[j][i]=0
    return m

