var N=parseInt(readline());
var candidates=new Array();
for (var i=0;i<N;i++){
    candidates.push(readline().split(' '));
}
console.error(candidates);
var res = candidates.filter(([,score])=>score>=900).sort((a, b) => {
      const [idA, scoreA, challengesA, postsA] = a;
      const [idB, scoreB, challengesB, postsB] = b;

      if (scoreA > scoreB) return -1;
      else if (scoreA < scoreB) return 1;
      else if (challengesA > challengesB) return -1;
      else if (challengesA < challengesB) return 1;
      else if (postsA > postsB) return -1;
      else if (postsA < postsB) return 1;
      else if (parseInt(idA) < parseInt(idB)) return -1;
      else if (parseInt(idA) > parseInt(idB)) return 1;
      else return 0;
    })
    .map(([id]) => id)
    .slice(0, 7);
console.log(res.length>0?res.join(' '):"None");
