using System;
using System.Linq;
using System.IO;
using System.Text;
using System.Collections;
using System.Collections.Generic;

class Solution
{
    static void Main(string[] args)
    {
        int N = int.Parse(Console.ReadLine());
        List<string> lines = new List<string>();

        // Simply collect infos
        int indexOfPerson = 0;
        int heightOfPerson = 0;
        for (int i = 0; i < N; i++)
        {
            lines.Add(Console.ReadLine());
            if(lines[i].Contains("o"))
            {
                indexOfPerson = lines[i].IndexOf("o");
                heightOfPerson = N - i - 1;
            }
        }

        Console.Error.WriteLine(indexOfPerson + heightOfPerson);
        // Go from the index of the person
        for(int i = indexOfPerson + heightOfPerson; i < lines[N - 1].Length; ++i)
        {
            if(lines[N - 1][i] == '~')
            {
                // If the distance matches, we have to further check the stacked trees
                int distance = i - indexOfPerson;
                if(distance == heightOfPerson)
                {
                    // Diagonally going backwards
                    int diff = 1;
                    for(int j = N - 2; j >= N - distance; j--)
                    {                   
                        string line = lines[j];
                        if(line[i - diff--] == 'T')
                        {
                            Console.WriteLine("Big tree");
                            return;
                        }
                    }

                    Console.WriteLine("Land");
                    return;
                }
            }
        }
        Console.WriteLine("Tree");
    }
}
