fn main() {
  let n = input!(usize);
  let zombie_vec: Vec<_> = (0..n).map(|_| input!(f64)).collect();
  let mut pos_vec: Vec<_> = vec![9.0_f64; n];

  while pos_vec.clone().into_iter().all(|x| x > 2.0) {
    pos_vec = pos_vec
      .clone()
      .into_iter()
      .zip(zombie_vec.clone().into_iter())
      .map(|(curr, z)| curr - z)
      .collect();
  }

  let front = pos_vec.clone().into_iter().fold(f64::NAN, f64::min);

  let dead_vec = pos_vec
    .into_iter()
    .filter(|&x| x - 1.0 <= front)
    .collect::<Vec<_>>();
  let alive_count = n - dead_vec.len();

  match alive_count {
    0 => {
      let back = dead_vec.clone().into_iter().fold(f64::NAN, f64::max);
      print!("DEAD {:.2}", back - front)
    }
    _ => print!("ALIVE {}", alive_count),
  }
}

#[macro_export]
macro_rules! input {
  () => {{
    input!(String)
  }};

  ($t:ty) => {{
    use std::io::BufRead;
    std::io::stdin()
      .lock()
      .lines()
      .next()
      .unwrap()
      .unwrap()
      .parse::<$t>()
      .unwrap()
  }};
}

#[macro_export]
macro_rules! tup {
  ($s:expr) => {
    tup![$s, String]
  };

  ($s:expr, $t:ident) => {
    tup![$s, $t, " "]
  };

  ($s:expr, $d:expr) => {
    tup![$s, String, $d]
  };

  ($s:expr, $t:ident, $d:expr) => {{
    use itertools::Itertools;
    $s.split($d)
      .map(|s| s.parse::<$t>().unwrap())
      .collect_tuple()
      .unwrap()
  }};
}
