N=int(input())
S=int(input())
O=input()

def mini(o,n):
    lg=len(o)
    seg=lg//n
    if n==1: return int(o)
    return min(int(o[i:i+seg]) for i in range(lg-seg))


def f(p,n,s,o):

    if s==0 and o=="":
        return [p]
    if n==1:
        if s==int(o): return [p+"+"+o]
        else: return False
    i=1
    sol=[]
    while i<len(o)-n+2 and int(o[:i])<s:
        if mini(o[i:],n-1)<=s-int(o[:i]) :
            c=f(p+"+"+o[:i],n-1,s-int(o[:i]),o[i:])
            if c: sol+=c
        i+=1


    if len(sol)==0: return False
    else: return sol

sols= f("",N,S,O)

if not sols: print("No solution")
else:
    sols.sort()
    for sol in sols:
        print(sol[1:]+"="+str(S))
