using System;
using System.Collections.Generic;
using System.Linq;

static class Scan
{
    public static IEnumerable<IEnumerable<T>> ScanBy<T, TKey>(this IEnumerable<T> enumerable,
                                                              Func<T, TKey> keySelector)
    {
        return enumerable.OrderBy(keySelector)
                         .GroupBy(keySelector);
    }

    public static IEnumerable<IEnumerable<T>> ThenBy<T, TKey>(this IEnumerable<IEnumerable<T>> enumerable,
                                                              Func<T, TKey> keySelector)
    {
        var b = true;
        foreach (var e in enumerable)
        {
            foreach (var t in (b ? e.OrderBy(keySelector) : e.OrderByDescending(keySelector)).GroupBy(keySelector))
            {
                yield return t;
            }

            b = !b;
        }
    }
}

static class Program
{
    static void Main()
    {
        var N = int.Parse(Console.ReadLine());
        var s = Enumerable.Range(0, N)
                          .Select(_ => Console.ReadLine().Split())
                          .Select(a => (name: a[0], x: int.Parse(a[1]), y: int.Parse(a[2])))
                          .ScanBy(t => t.x)
                          .ThenBy(t => t.y)
                          .SelectMany(e => e)
                          .Select(t => t.name);

        Console.WriteLine(string.Join(",", s));

        Console.ReadLine();
    }
}
