#include<cstdio>
#include<cstring>
long long f[22][3],pre[22][3];
void predo()
{
	pre[0][0]=1;
	for(int i=0; i<20; i++)
	{
		pre[i+1][0]+=pre[i][0]*8;
		pre[i+1][1]+=(pre[i][0]+pre[i][1]*9);
		pre[i+1][2]+=(pre[i][0]+pre[i][2]*9);
	}
}
long long solve(long long lim)
{
	int tmp[25],cnt=0;
	while(lim)
	{
		tmp[++cnt]=lim%10;
		lim/=10;
	}
	memset(f,0,sizeof(f));
	long long ans=0;
	bool now6=false,now8=false;
	for(int i=cnt; i; i--)
	{
		if(now6 && now8) break;
		int nowtp=tmp[i];
		for(int j=0; j<nowtp; j++)
		{
			if(j!=6 && j!=8)
			{
				if(!now6 && !now8) ans+=pre[i-1][1]+pre[i-1][2];
				if(now6) ans+=pre[i-1][1]+pre[i-1][0];
				if(now8) ans+=pre[i-1][2]+pre[i-1][0];
			}
			else if(j==6 && !now8) ans+=pre[i-1][0]+pre[i-1][1];
			else if(j==8 && !now6) ans+=pre[i-1][0]+pre[i-1][2];
		}
		if(nowtp==6) now6=true;
		if(nowtp==8) now8=true;
	}
	if(now6^now8) ans++;
	return ans;
}
int main()
{
	predo();
	long long l,r;
	scanf("%lld%lld",&l,&r);
	printf("%lld\n",solve(r)-solve(l-1));
	return 0;
}
