#include <iostream>
#include <vector>
using namespace std;

struct coord { int x, y; char keys; string s; };

const string dirs[6] = { "L", "R", "UL", "UR", "DL", "DR" };
string grid[40];
vector<coord> v;

bool block(char cell, char keys) { return cell=='#' || 'A'<=cell&&cell<='D'&&!(keys&1<<cell-'A');}
bool contains(const coord& c) {
    bool found = false;
    for (int i=0; i<v.size() && !found; ++i)
        found = (v[i].x<<16|v[i].y<<8|v[i].keys)==(c.x<<16|c.y<<8|c.keys);
    return found;
}
coord nextCell(const coord& c, const string& dir) {
    coord ret = c;
    if (dir == "L") {
        if (!block(grid[ret.y][ret.x-1], c.keys)) {
            ret.x--;
            while ('_' == grid[ret.y][ret.x] && !block(grid[ret.y][ret.x-1], c.keys))
                ret.x--;
        }
    }
    else if (dir == "R") {
        if (!block(grid[ret.y][ret.x+1], c.keys)) {
            ret.x++;
            while ('_' == grid[ret.y][ret.x] && !block(grid[ret.y][ret.x+1], c.keys))
                ret.x++;
        }
    }
    else if (dir == "UL") {
        if (!block(grid[ret.y-1][ret.x-!(ret.y&1)], c.keys)) {
            ret.x -= !(ret.y&1);
            ret.y--;
            while ('_' == grid[ret.y][ret.x] && !block(grid[ret.y-1][ret.x-!(ret.y&1)], c.keys))
                ret.x -= !(ret.y&1), ret.y--;
        }
    }
    else if (dir == "UR") {
        if (!block(grid[ret.y-1][ret.x+(ret.y&1)], c.keys)) {
            ret.x += ret.y&1;
            ret.y--;
            while ('_' == grid[ret.y][ret.x] && !block(grid[ret.y-1][ret.x+(ret.y&1)], c.keys))
                ret.x += ret.y&1, ret.y--;
        }
    }
    else if (dir == "DL") {
        if (!block(grid[ret.y+1][ret.x-!(ret.y&1)], c.keys)) {
            ret.x -= !(ret.y&1);
            ret.y++;
            while ('_' == grid[ret.y][ret.x] && !block(grid[ret.y+1][ret.x-!(ret.y&1)], c.keys))
                ret.x -= !(ret.y&1), ret.y++;
        }
    }
    else if (dir == "DR") {
        if (!block(grid[ret.y+1][ret.x+(ret.y&1)], c.keys)) {
            ret.x += ret.y&1;
            ret.y++;
            while ('_' == grid[ret.y][ret.x] && !block(grid[ret.y+1][ret.x+(ret.y&1)], c.keys))
                ret.x += ret.y&1, ret.y++;
        }
    }
    ret.s = ret.s + " " + dir;
    return ret;
}
main()
{
    // Read inputs
    int w, h;
    cin >> w >> h; cin.ignore();
    coord S = {-1, -1, 0, ""};
    for (int i=0; i<h; ++i) {
        getline(cin, grid[i]);
        // Find start position
        if (S.x == -1)
            for (int j=0; j<w && S.x==-1; ++j)
                if (grid[i][j] == 'S')
                    S = {j, i, 0, ""};
    }
    // BFS
    int it = 0;
    v.push_back(S);
    string answer;
    while (answer.empty()) {
        coord cur = v[it++];
        for (int i=0; i<6; ++i) {
            coord next = nextCell(cur, dirs[i]);
            char h = grid[next.y][next.x];
            if ('a'<=h && h<='d') next.keys |= 1<<h-'a';
            if ('E' == h) answer = next.s;
            else if (!contains(next)) v.push_back(next);
        }
    }
    // Print output
    cout << answer.c_str()+1 << endl;
}
