using System;
using System.Linq;
using System.IO;
using System.Text;
using System.Collections;
using System.Collections.Generic;

class Solution {
    static void Main(string[] args) {
        string phrase = Console.ReadLine();

        var spaceIndices = phrase.ToCharArray()
            .Select((c, i) => new { Chr = c, Idx = i })
            .Where(_ => _.Chr == ' ')
            .Select(_ => _.Idx)
            .ToList();
        spaceIndices.Insert(0, -1);
        spaceIndices.Add(phrase.Length);
        
        var wordLengths = Enumerable.Range(1, spaceIndices.Count-1)
            .Select(i => spaceIndices[i] - spaceIndices[i-1] - 1)
            .Reverse()
            .ToList();
            
        var revised = phrase.Replace(" ", string.Empty);
        
        var count = 0;
        var added = 0;
        for (var i = 0; added < wordLengths.Count - 1; i++) {
            if (count == wordLengths[added]) {
                added++;
                revised = revised.Insert(i+added-1, " ");
                count = 0;
            }
            count++;
        }
        
        var fourthLetters = revised.ToCharArray()
            .Select((c, i) => new { Chr = c, Idx = i })
            .Where(_ => ((int)_.Chr) % 4 == 0 && _.Chr != ' ')
            .ToList();
            
        if (fourthLetters.Any()) {
            var tmpList = fourthLetters.ToList();
            var tmp = fourthLetters.Last();
            fourthLetters.Insert(0, tmp);
            fourthLetters.RemoveAt(fourthLetters.Count-1);
            fourthLetters = tmpList
                .Select((_, i) => new { Chr = fourthLetters[i].Chr, Idx = _.Idx })
                .ToList();
        
            fourthLetters = fourthLetters
                .Select((_, i) => new { Chr = fourthLetters[i].Chr, Idx = _.Idx })
                .ToList();
            
            var array = revised.ToCharArray();
            foreach (var letter in fourthLetters) {
                array[letter.Idx] = letter.Chr;
            }
            
            revised = new string(array);
        }
        
        
        var thirdLetters = revised.ToCharArray()
            .Select((c, i) => new { Chr = c, Idx = i })
            .Where(_ => (((int)_.Chr)-1) % 3 == 0)
            .ToList();
            
        if (thirdLetters.Any()) {
            var tmpList = thirdLetters.ToList();
            var tmp = thirdLetters.First();
            thirdLetters.Add(tmp);
            thirdLetters.RemoveAt(0);
            thirdLetters = tmpList
                .Select((_, i) => new { Chr = thirdLetters[i].Chr, Idx = _.Idx })
                .ToList();

            var array = revised.ToCharArray();
            foreach (var letter in thirdLetters) {
                array[letter.Idx] = letter.Chr;
            }
            
            revised = new string(array);
        }
        
        var secondLetters = revised.ToCharArray()
            .Select((c, i) => new { Chr = c, Idx = i })
            .Where(_ => ((int)_.Chr) % 2 == 0 && _.Chr != ' ')
            .ToList();
            
        if (secondLetters.Any()) {
            secondLetters = secondLetters.AsEnumerable().Reverse()
                .Select((_, i) => new { Chr = secondLetters[i].Chr, Idx = _.Idx })
                .ToList();
        
            var array = revised.ToCharArray();
            foreach (var letter in secondLetters) {
                array[letter.Idx] = letter.Chr;
            }
        
            revised = new string(array);
        }
        
        Console.WriteLine(revised);
    }
}
