cache = {0: 0, 1: 1}

def fibonacci(n):
    if n in cache:
        return cache[n]
    cache[n] = fibonacci(n - 1) + fibonacci(n - 2) 
    return cache[n]

current_fib_index = 0
def fibonacci_gen():
    global current_fib_index
    while True:
        yield fibonacci(current_fib_index)
        current_fib_index += 1

def pyramid(n):
    gen = fibonacci_gen()
    for i in range(1, n+1):
        print(" ".join(str(next(gen)) for _ in range(i)))


n = int(input())
pyramid(n)
