import re

s = re.findall('([a-z])([a-z])', input())
n = int(input())
m = [[*input()] for _ in range(n)]

chk = {}

for a, b in s:
    if a == b:
        continue
    if a in chk and chk[a] != b:
        print('ERROR')
        break
    chk[a] = b
else:
    for i in chk:
        c = chk[i]
        if c not in chk:
            continue
        count = 1
        while count < len(chk) and chk[c] in chk:
            c = chk[c]
            count += 1
        if count == len(chk):
            print('ERROR')
            break
    else:
        for _ in range(len(chk)):
            for i in range(n):
                for j in range(n):
                    if m[i][j] in chk:
                        m[i][j] = chk[m[i][j]]
        [print(*i, sep = '') for i in m]
