using System;
using System.Linq;
using System.IO;
using System.Text;
using System.Collections;
using System.Collections.Generic;

public class Program
{
    public static void Main()
	{
        string streamOfConsciousness = Console.ReadLine();
        int bustThreshold = int.Parse(Console.ReadLine());
		var listChunks = streamOfConsciousness.Split('.');

		var dic = new Dictionary<int,int>();
		dic.Add(10,16); // means there are 16 cards that have the value of "10"
		for (int i = 1 ; i <= 9 ; i++)
		{
			dic.Add(i,4); // means (for example) there are 4 cards that have the value of "7"
		}

		int leftInDeck = 52;

		foreach (var item in listChunks)
		{
			bool isCards = true;
			foreach (var ltr in item)
			{
				if ("AKQJT98765432A".Contains(ltr) == false)
				{
					isCards = false;
				}
			}
			if (isCards)
			{
				Console.Error.WriteLine("This is cards : " + item);
				foreach (var ltr in item)
				{
					leftInDeck--;
					if ("KQJT".Contains(ltr))
					{
						dic[10] = dic[10]-1;
					}
					else if (ltr == 'A')
					{
						dic[1] = dic[1]-1;
					}
					else
					{
						int num = (int)(ltr-'0');
						dic[num] = dic[num] - 1;
					}
				}
			}
		}
		int counter = 0;
		foreach (var kvp in dic)
		{
			Console.Error.WriteLine(kvp.Key + " " + kvp.Value);	
			if (kvp.Key < bustThreshold)
			{
				counter = counter + kvp.Value;
			}
		}
		Console.Error.WriteLine("counter = " + counter);;

		Console.Error.WriteLine("leftInDeck = " + leftInDeck);;

		double ans = counter * 100.0 / leftInDeck;
		Console.WriteLine(Math.Round(ans,0) + "%");
    }
}
