n = int(input())
complete_words = []
for i in range(n):
    complete_words.append(input())

m = int(input())
for i in range(m):
    incomplete_word = input()
    idx=[]
    for o in range(len(incomplete_word)):
        if incomplete_word[o] == "_": idx.append(o)
    for j in complete_words:
        old = j
        for p in idx:
            old = old[:p] + "_" + old[p+1:]
        if incomplete_word == old: print(j)
