/**
 * Auto-generated code below aims at helping you parse
 * the standard input according to the problem statement.
 **/

var ROUNDS = parseInt(readline());
var CASH = parseInt(readline());
for (var i = 0; i < ROUNDS; i++) {
    var action = readline().split(" ");

    var bet = Math.ceil(CASH * 0.25);
	var ball = parseInt(action[0])

	var multiplier = 0;

    switch (action[1]) {
        case 'EVEN': 
            if (ball != 0 && ball % 2 == 0) multiplier = 1;
            break;
        case 'ODD': 
            if (ball % 2 == 1) multiplier = 1;
            break;
        case 'PLAIN': 
            var number = parseInt(action[2])
            if (number == ball) {
                multiplier = 35;
            }
            break;

    }

    CASH += (multiplier == 0 ? -bet : multiplier * bet);
    printErr("#" + (i + 1) + " " + action.join(" "));
    printErr(CASH);
}

// Write an action using print()
// To debug: printErr('Debug messages...');

print(CASH);
