using System;
using System.Drawing;
using System.Linq;
using System.IO;
using System.Text;
using System.Collections;
using System.Collections.Generic;

class Solution {
    private static readonly Dictionary<string, Point> ArrowDirections =
        new Dictionary<string, Point> {
            {"W", new Point(-1, 0)}, {"E", new Point(1, 0)},
            {"N", new Point(0, -1)}, {"S", new Point(0, 1)}
        };

    private static int _width;
    private static int _height;

    static void Main(string[] args) {
        string[] inputs;
        inputs = Console.ReadLine().Split(' ');
        _width = int.Parse(inputs[0]);
        _height = int.Parse(inputs[1]);
        int numPlayers = int.Parse(Console.ReadLine());
        int numDoors = int.Parse(Console.ReadLine());
        int numTurns = int.Parse(Console.ReadLine());
        var rockets = new List<Point>();
        for (int i = 0; i < numPlayers; i++) {
            inputs = Console.ReadLine().Split(' ');
            int rX = int.Parse(inputs[0]);
            int rY = int.Parse(inputs[1]);
            rockets.Add(new Point(rX, rY));
        }
        var doors = new List<Director>();
        for (int i = 0; i < numDoors; i++) {
            inputs = Console.ReadLine().Split(' ');
            int coord = int.Parse(inputs[0]);
            string wall = inputs[1];
            doors.Add(DirectorFor(coord, wall));
        }

        var animals = new List<Animal>();
        var arrows = new List<Director>();
        var scores = Enumerable.Repeat(0, numPlayers).ToList();
        for (int i = 0; i <= numTurns; i++) {
            // add
            if ((i+1) % 10 == 0) animals.AddRange(doors.Select(_ => new Cat(_)));
            else animals.AddRange(doors.Select(_ => new Mouse(_)));

            // move
            animals.ForEach(_ => _.Move());

            // score
            scores = scores.Select((s, j) => Math.Max(0,
                    s + animals.Count(m => !m.IsCat && m.Location == rockets[j]) -
                    (10 * animals.Count(c => c.IsCat && c.Location == rockets[j]))))
                .ToList();

            if (i == numTurns) break;

            // remove for rockets, pits, and eaten
            animals.RemoveAll(a => rockets.Any(r => r == a.Location));
            animals.RemoveAll(_ => IsPit(_.Location));
            animals.RemoveAll(m => !m.IsCat &&
                animals.Any(c => c.IsCat &&
                    (c.Location == m.Location || 
                    (c.Location == m.PreviousLocation &&
                     c.PreviousLocation == m.Location))));

            // redirect
            arrows.ForEach(_ => animals.Where(a => a.Location == _.Location).ToList()
                .ForEach(a => a.Direction = _.Direction));
            animals.ForEach(a => RedirectByWall(a));

            // add arrow
            inputs = Console.ReadLine().Split(' ');
            int tX = int.Parse(inputs[0]);
            int tY = int.Parse(inputs[1]);
            string direction = inputs[2];
            arrows.Add(new Director(new Point(tX, tY), ArrowDirections[direction]));

            // remove old arrow
            if (arrows.Count > numPlayers * 3) arrows.RemoveAt(0);
        }

        Console.WriteLine(string.Join(System.Environment.NewLine, scores));
    }

    private static Director DirectorFor(int coord, string wall) {
        switch (wall) {
            case "N":
                return new Director(new Point(coord, -1), new Point(0, 1));
            case "S":
                return new Director(new Point(coord, _height), new Point(0, -1));
            case "E":
                return new Director(new Point(_width, coord), new Point(-1, 0));
            case "W":
                return new Director(new Point(-1, coord), new Point(1, 0));
        }
        throw new NotImplementedException();
    }

    private static bool IsPit(Point loc) {
        return (loc.X == 0 && loc.Y == 0) ||
               (loc.X == _width-1 && loc.Y == 0) ||
               (loc.X == 0 && loc.Y == _height-1) ||
               (loc.X == _width-1 && loc.Y == _height-1);
    }

    private static void RedirectByWall(Animal a) {
        if (a.Location.X == 0 && a.Direction.X == -1) a.Direction = new Point(0, 1);
        if (a.Location.Y == 0 && a.Direction.Y == -1) a.Direction = new Point(-1, 0);
        if (a.Location.X == _width-1 && a.Direction.X == 1) a.Direction = new Point(0, -1);
        if (a.Location.Y == _height-1 && a.Direction.Y == 1) a.Direction = new Point(1, 0);
    }

    private class Director {
        public Point Location { get; private set; }
        public Point Direction { get; private set; }
        public Director(Point location, Point direction) {
            Location = location;
            Direction = direction;
        }
    }

    private abstract class Animal {
        public Point Location { get; private set; }
        public Point Direction { get; set; }
        public Point PreviousLocation { get; private set; }
        public bool IsCat { get; protected set; }

        public Animal(Director dir) {
            Location = new Point(dir.Location.X, dir.Location.Y);
            Direction = new Point(dir.Direction.X, dir.Direction.Y);
            PreviousLocation = new Point(-1, -1);
        }

        public void Move() {
            PreviousLocation = Location;
            Location = new Point(Location.X + Direction.X, Location.Y + Direction.Y);
        }
    }

    private class Cat : Animal {
        public Cat(Director dir) : base(dir) {
            IsCat = true;
        }
    }

    private class Mouse : Animal {
        public Mouse(Director dir) : base(dir) {
            IsCat = false;
        }
    }
}
