using System;
using System.Linq;
using System.IO;
using System.Text;
using System.Collections;
using System.Collections.Generic;

/**
 * Auto-generated code below aims at helping you parse
 * the standard input according to the problem statement.
 **/
class Solution
{
    static void Main(string[] args)
    {
        int N = int.Parse(Console.ReadLine());
        int firstPlayer = int.Parse(Console.ReadLine());
        int turns = int.Parse(Console.ReadLine());

        int direction = 1;
        int ballIndex = firstPlayer;

        for (int i = 0; i < turns; i++)
        {
            string turn = Console.ReadLine();
            if (i == 0) direction = turn.Split(' ')[1] == "right" ? 1 : -1;
            else if (turn == "Wazz") direction *= -1;

            ballIndex = (ballIndex + direction)%N;
            if (ballIndex < 0) ballIndex += N;
        }

        Console.WriteLine(ballIndex);
    }
}
