import numpy as np
from copy import deepcopy

class Grid:
    def __init__(self, rows, cols):
        self.rows = rows
        self.cols = cols
        self.grid = np.zeros((self.rows, self.cols))
        self.current_affected_cells = []
        self.next_affected_cells = []

    def input_to_grid(self, _input):
        for row in range(len(_input) // 3):
            for col in range(len(_input[row]) // 3):
                pass


    def connected_cells(self, cell):
        row, col = cell
        connected_cells = []
        if self.grid[cell] == 0:
            if row - 1 >= 0 and self.grid[row - 1, col] in (1, 2):
                connected_cells.append((row - 1, col))
            if col + 1 < self.grid.shape[1] and self.grid[row, col + 1] in (2, 3):
                connected_cells.append((row, col + 1))

        elif self.grid[cell] == 1:
            if row + 1 < self.grid.shape[0] and self.grid[row + 1, col] in (0, 3):
                connected_cells.append((row + 1, col))
            if col + 1 < self.grid.shape[1] and self.grid[row, col + 1] in (2, 3):
                connected_cells.append((row, col + 1))

        elif self.grid[cell] == 2:
            if row + 1 < self.grid.shape[0] and self.grid[row + 1, col] in (0, 3):
                connected_cells.append((row + 1, col))
            if col - 1 >= 0 and self.grid[row, col - 1] in (0, 1):
                connected_cells.append((row, col - 1))

        elif self.grid[cell] == 3:
            if row - 1 >= 0 and self.grid[row - 1, col] in (1, 2):
                connected_cells.append((row - 1, col))
            if col - 1 >= 0 and self.grid[row, col - 1] in (0, 1):
                connected_cells.append((row, col - 1))

        return connected_cells


    def rotate_cell(self, cell, sens):
        self.grid[cell] += sens
        if self.grid[cell] == -1:
            self.grid[cell] = 3
        if self.grid[cell] == 4:
            self.grid[cell] = 0 

    def find_best_move(self):        
        best_move = None
        best_score = 0

        for row in (range(self.rows)):
            for col in range(self.cols):

                copy = deepcopy(self)
                copy.current_affected_cells = [(row, col)]
                sens = 1           
                score = 0

                while copy.current_affected_cells:
                    for cell in set(copy.current_affected_cells):
                        copy.rotate_cell(cell, sens)
                        copy.next_affected_cells += copy.connected_cells(cell)
                        score += 1                    

                    copy.current_affected_cells = copy.next_affected_cells[:]
                    copy.next_affected_cells = []
                    sens = -sens

                if score > best_score:
                    best_score = score
                    best_move = (row, col)


        return best_score, best_move

r = int(input())
b = int(input())
grid = Grid(r, b)

for row in range(r):
    rows = []
    for j in range(3):
        rows.append(input())
    for col in range(b):

        if rows[0][col * 5:col * 5 + 5] == "  |  "\
        and rows[1][col * 5:col * 5 + 5] == "  +--"\
        and rows[2][col * 5:col * 5 + 5] == "     ":
            grid.grid[row, col] = 0

        elif rows[0][col * 5:col * 5 + 5] == "     "\
        and rows[1][col * 5:col * 5 + 5] == "  +--"\
        and rows[2][col * 5:col * 5 + 5] == "  |  ":
            grid.grid[row, col] = 1

        elif rows[0][col * 5:col * 5 + 5] == "     "\
        and rows[1][col * 5:col * 5 + 5] == "--+  "\
        and rows[2][col * 5:col * 5 + 5] == "  |  ":
            grid.grid[row, col] = 2

        elif rows[0][col * 5:col * 5 + 5] == "  |  "\
        and rows[1][col * 5:col * 5 + 5] == "--+  "\
        and rows[2][col * 5:col * 5 + 5] == "     ":
            grid.grid[row, col] = 3

best_score, (best_row, best_col) = grid.find_best_move()

print(f"{best_row} {best_col}")
print(f"{best_score}")
