/**
 * Auto-generated code below aims at helping you parse
 * the standard input according to the problem statement.
 **/

var N = parseInt(readline());
var B = readline().split("");

var D = []; //Distance

//Crawl forward and set distances
var last = -1;
for (var i = 0; i < N; ++i){
    D[i] = (B[i] == '!' ? -1 : (last == -1 ? 100 : i - last));
    if (B[i] == '!') last = i;
}

//Crawl backwards and set distances
last = -1;
var index = -1;
var best = -1;
for (var i = N-1; i >= 0; --i){
    D[i] = Math.min(D[i], (B[i] == '!' ? -1 : (last == -1 ? 100 : last - i)));
    if (B[i] == '!') last = i;

    if ((index == -1 || D[i] >= best) && B[i] != '!'){
        index = i;
        best = D[i];
    }   
}

print(index);
