#include <stdlib.h>
#include <stdio.h>
#include <string.h>
#include <stdbool.h>

/**
 * Auto-generated code below aims at helping you parse
 * the standard input according to the problem statement.
 **/

int main()
{
    char N[7];
    scanf("%[^\n]", N);

    int r[7] = {1, 2, 3, 4, 5, 6, 7};
    int s[9] = {1, 2, 3, 4, 5, 6, 7, 8, 9};

    int floor = -1;
    for(int i = 0; N[i]; i++)
    {
        if(N[i] == 'f')
            floor = N[i+1] - '0';
        if(N[i] == 'r')
        {
            r[0] = N[i+1] - '0';
            r[1] = -1;
        }
        if(N[i] == 's')
        {
            s[0] = N[i+1] - '0';
            s[1] = -1;
        }
    }

    for(int i = 0; i < 7 && r[i] != -1; i++)
    {
        for(int j = 0; j < 9 && s[j] != -1; j++)
        {
            printf("10.1%i.%i.%i\n", floor, r[i], s[j]);
        }
    }

    return 0;
}
