bool solution(vector<int> &vals, int n, int sum){

    int DP[n+1][sum+1];

    for (int i = 0; i <= n; i++) DP[i][0] = true;
    for (int i = 1; i <= sum; i++) DP[0][i]= false;

    for (int i = 1; i <= n; i++) {
        for (int j = 1; j <= sum; j++) {
            if (j<vals[i-1]) DP[i][j] = DP[i-1][j];
            if (j>= vals[i-1]) DP[i][j] = (DP[i-1][j] || DP[i - 1][j-vals[i-1]]);
        }
    }
    return DP[n][sum];
}


