#include <cmath>
#include <cstdio>
#include <vector>
#include <iostream>
#include <algorithm>
using namespace std;


int main() {
    long b,w;
    cin>>b>>w;
    long x,y,z;
    cin>>x>>y>>z;
    long m1,m2,m3;
    m1=b*x+w*y;
    m2=(b+w)*x+w*z;
    m3=(b+w)*y+b*z;
    if(m1<=m2 && m1<=m3)
        {
        cout<<m1;
    }
    else if(m2<m1 && m2<m3)
        {
            cout<<m2;
        }
    else
        {
        cout<<m3;
    }
    cout<<endl;
    
    return 0;
}
