using System;

class Solution
{
    private const string alph = "ABCDEFGHIJKLMNOPQRSTUVWXYZ ";

    static void Main(string[] args)
    {
        string m = Console.ReadLine();
        string c = Console.ReadLine();

        Console.WriteLine(Decrypt(m, char.Parse(c)));
    }

    public static string Decrypt(string hash, char fdc){
        string msg = "";
        int shift = alph.IndexOf(fdc) - alph.IndexOf(hash[0]);
        if(shift < 0) {
            shift += alph.Length;
        }
        string nAlph = alph.Substring(shift) + alph.Substring(0, shift);
        foreach(char c in hash){
            msg += nAlph[alph.IndexOf(c)];
        }
        return msg;
    }
}
