const state = readline()
const n = parseInt(readline())
const shifts = readline().match(/.{2}/g)

//Fill initial array of length n with known starting state value
let arr = new Array(n).fill(state=='H'?1:0)

for(let i = 0; i < shifts.length; i++){
    //Grab the value (HIGH or LOW) from each section of the separated shifts array
    v = shifts[i][0]
    //Grab the number of times the clock will be toggled
    N = +shifts[i][1]

    //Shift LOW (0) in from the left
    if(v == 'L'){
        for(let j = 0; j < N; j++){
            arr.unshift(0)
            arr.pop()//Excess least significant bits fall into the bit bucket
        }
    }
    //Shift HIGH (1) in from the left
    if(v == 'H'){
        for(let j = 0; j < N; j++){
            arr.unshift(1)
            arr.pop()//Excess least significant bits fall into the bit bucket
        }
    }
}

//Print the array after shifts are completed
console.log(arr.join``)
