import java.util.ArrayList;
import java.util.List;
import java.util.Scanner;

public class Solution {

	public static void main(String[] args) {
		Scanner in = new Scanner(System.in);
		Board b = new Board();

		int R = in.nextInt();
		int P = in.nextInt();
		in.nextLine();
		for (int j = 0; j < R; j++) {
			String s = in.nextLine();
			b.addShape(s);
		}
		for (int j = 0; j < P; j++) {
			String[] ss = in.nextLine().split("\\s");
			int x = Integer.parseInt(ss[0]);
			int y = Integer.parseInt(ss[1]);
			b.queryColor(x, y);
		}
		in.close();
	}
}

class Board {
	List<Shape> sList = new ArrayList<>();

	public void addShape(String s) {
		sList.add(new Shape(s));
	}

	public void queryColor(int x, int y) {
		int count = 0; // how many shapes contain the target point
		int r = 0, g = 0, b = 0;
		for (Shape sh : sList) {
			// 0: outside, 1: on edge, 2: inside
			int status = sh.contains(x, y);
			if (status == 1) {
				System.out.println("(0, 0, 0)"); // black
				return;
			} else if (status == 2) {
				r += sh.R;
				g += sh.G;
				b += sh.B;
				count++;
			}
		}
		if (count == 0) {
			System.out.println("(255, 255, 255)"); // white
			return;
		}

		r = Math.round((float) r / count);
		g = Math.round((float) g / count);
		b = Math.round((float) b / count);
		System.out.printf("(%d, %d, %d)\n", r, g, b);
	}
}

class Shape {
	int type; // 1: square or 0: circle
	int x0, y0; // origin
	int len;
	int R, G, B;

	public Shape(String s) {
		String[] ss = s.split("\\s");
		type = ss[0].equals("CIRCLE") ? 0 : 1;
		x0 = Integer.parseInt(ss[1]);
		y0 = Integer.parseInt(ss[2]);
		len = Integer.parseInt(ss[3]);
		R = Integer.parseInt(ss[4]);
		G = Integer.parseInt(ss[5]);
		B = Integer.parseInt(ss[6]);
	}

	// 0: outside, 1: on edge, 2: inside
	public int contains(int x, int y) {
		// square
		if (type == 1) {
			int x2 = x0 + len; // the opp side
			int y2 = y0 + len;

			if ((x == x0 || x == x2) && (y >= y0 && y <= y2)) {
				return 1;
			}
			if ((y == y0 || y == y2) && (x >= x0 && x <= x2)) {
				return 1;
			}
			if (x > x0 && x < x2 && y > y0 && y < y2) {
				return 2;
			}
			return 0;
		}
		// circle
		else {
			int dx = x - x0;
			int dy = y - y0;
			int dx2 = dx * dx;
			int dy2 = dy * dy;
			int len2 = len * len;
			if (dx2 + dy2 == len2) {
				return 1;
			}
			if (dx2 + dy2 < len2) {
				return 2;
			}
			return 0;
		}
	}
}
