#include <iostream>
#include <string>
#include <vector>
#include <algorithm>

using namespace std;

int main()
{
    int N;
    int x[100][100];
    int y[100][100];
    cin>>N;
    int points[N];
    for (int i=0;i<N;i++){
        for (int j=0;j<5;j++){
            cin>>x[i][j]>>y[i][j];}}
    for (int i=0;i<N;i++){
        points[i]=0;
        for (int j=0;j<5;j++){
                if (x[i][j]*x[i][j] + y[i][j] * y[i][j]<=1*1 && x[i][j]*x[i][j] + y[i][j] * y[i][j]>=0){
                    points[i]=points[i] + 10;}
               else if (x[i][j]*x[i][j] + y[i][j] * y[i][j]<=2*2 && x[i][j]*x[i][j] + y[i][j] * y[i][j]>1*1){
                        points[i]+=9;}
                else if(x[i][j]*x[i][j] + y[i][j] * y[i][j]<=3*3 && x[i][j]*x[i][j] + y[i][j] * y[i][j]>2*2){
                        points[i]+=8;}
                else if(x[i][j]*x[i][j] + y[i][j] * y[i][j]<=4*4 && x[i][j]*x[i][j] + y[i][j] * y[i][j]>3*3){
                        points[i]+=7;}
                else if(x[i][j]*x[i][j] + y[i][j] * y[i][j]<=5*5 && x[i][j]*x[i][j] + y[i][j] * y[i][j]>4*4){
                        points[i]+=6;}
                else if(x[i][j]*x[i][j] + y[i][j] * y[i][j]<=6*6 && x[i][j]*x[i][j] + y[i][j] * y[i][j]>5*5){
                        points[i]+=5;}
                else if(x[i][j]*x[i][j] + y[i][j] * y[i][j]<=7*7 && x[i][j]*x[i][j] + y[i][j] * y[i][j]>6*6){
                        points[i]+=4;}
                else if(x[i][j]*x[i][j] + y[i][j] * y[i][j]<=8*8 && x[i][j]*x[i][j] + y[i][j] * y[i][j]>7*7){
                        points[i]+=3;}
                else if(x[i][j]*x[i][j] + y[i][j] * y[i][j]<=9*9 && x[i][j]*x[i][j] + y[i][j] * y[i][j]>8*8){
                        points[i]+=2;}
                else if(x[i][j]*x[i][j] + y[i][j] * y[i][j]<=10*10 && x[i][j]*x[i][j] + y[i][j] * y[i][j]>9*9){
                        points[i]+=1;}
                else if (x[i][j]*x[i][j] + y[i][j] * y[i][j]>10*10){
                        points[i]+=0;}}}

        for (int i=0;i<N;i++){
        cout << points[i] <<endl;}
}
