s = input()
n = int(input())
y = x = 0;money = 50
check = {}
move = {'U': (-1, 0), 'D': (1, 0), 'R': (0, 1), 'L': (0, -1)}

for _ in range(n):
    m = input()
    *point, _, item = m.split(' ')
    point = *map(int, point),
    item = item if item.isalpha() else item[:-1]
    if item.isnumeric():
        item = int(item)
    check[point] = item

for i in s:
    my, mx = move[i]
    y += my;x += mx
    if (y, x) in check:
        item = check[(y, x)]
        if isinstance(item, int):
            money += item
            check[(y, x)] = 0
        else:
            if item[0] == 'g' and money >= 50:
                money -= 50
                continue
            print(f'{y} {x} {money}G {item}')
            exit()
print(f'GameClear {y} {x} {money}G')
