import java.awt.Point;
import java.awt.geom.Point2D;
import java.util.*;
import java.io.*;
import java.math.*;

class Solution {

    static int N;
    static List<Point> pontos = new ArrayList<>();
    static List<Vetor> vetores = new ArrayList<>();

    public static void main(String args[]) {
        Scanner in = new Scanner(System.in);
        N = in.nextInt();

        for (int i = 0; i < N; i++) {
            int x = in.nextInt();
            int y = in.nextInt();
            Point p = new Point(x, y);
            pontos.add(p);
            if (i > 0) {
                vetores.add(new Vetor(pontos.get(i - 1), p));
                if (i == N - 1) {
                    Vetor v = new Vetor(p, pontos.get(0));
                    v.normalizar();
                    vetores.add(v);
                }
            }
        }
        boolean b = definirSentido();
        System.out.println(b ? "CLOCKWISE" : "COUNTERCLOCKWISE");
    }

    static private boolean definirSentido() {
        double angulosDireita = 0;
        double angulosEsquerda = 0;
        for (int i = 1; i < N; i++) {
            Vetor v1 = vetores.get(i);
            Vetor v2 = vetores.get(i - 1);
            double angulo = v1.getAngulo(v2);
            double pv = Math.toDegrees(v1.produtoVetorial(v2));
            if (pv < 0) {
                angulosEsquerda += angulo;
            } else {
                angulosDireita += angulo;
            }
        }
        return angulosDireita > angulosEsquerda;
    }

    private static class Vetor {

        private double i;
        private double j;

        public Vetor(double i, double j) {
            this.i = i;
            this.j = j;
        }

        public Vetor(Point2D p1, Point2D p2) {
            this.i = p2.getX() - p1.getX();
            this.j = p2.getY() - p1.getY();
        }

        public void normalizar() {
            double modulo = getModulo();
            this.i = i / modulo;
            this.j = j / modulo;
        }

        public double getModulo() {
            return Math.sqrt(Math.pow(i, 2) + Math.pow(j, 2));
        }

        public double produtoEscalar(Vetor v) {
            return (this.i * v.i) + (this.j * v.j);
        }

        public double produtoVetorial(Vetor v) {
            return (this.i * v.j) - (this.j * v.i);
        }

        public double getAngulo(Vetor v) {
            return Math.acos(this.produtoEscalar(v) / (this.getModulo() * v.getModulo()));
        }

    }
}
