using System;
using System.Linq;
using System.IO;
using System.Text;
using System.Collections;
using System.Collections.Generic;

/**
 * Auto-generated code below aims at helping you parse
 * the standard input according to the problem statement.
 **/
class Solution
{
    static void Main(string[] args)
    {
        List<string> hourglass = new List<string>();

        // input

        for (int i = 0; i < 23; i++)
        {
            string hLine = Console.ReadLine();
            hourglass.Add(hLine);
        }
        int N = int.Parse(Console.ReadLine());

        // Note that:
        // index 1 to 10 - sand from top hourglass
        // index 12 to 21 - sand from bottom hourglass

        // determining how many bits of sand are in top hourglass and bottom hourglass

        int secondsInTop, secondsInBottom;

        secondsInTop = getSandAmount("TOP", hourglass);
        secondsInBottom = getSandAmount("BOTTOM", hourglass);

        if (secondsInTop + secondsInBottom != 100)
        {
            Console.Write("BROKEN HOURGLASS");
            return;
        }

        // new configuration

        secondsInTop -= N;
        secondsInBottom += N;

        secondsInTop = secondsInTop < 0 ? 0 : secondsInTop;
        secondsInBottom = secondsInBottom > 100 ? 100 : secondsInBottom;

        // reset hourglass

        hourglass = resetHourglass(hourglass);  // top is full, bottom is empty

        //TOP

        int index;

        while (secondsInTop < 100)  // we remove 100-secondsInTop spaces following the rule
        {
            index = getIndexOfNextLineSandIsSupposedToFallOn("TOP", hourglass);
            hourglass[index] = removeSand(hourglass[index]);

            secondsInTop++;
        }

        //BOTTOM

        int trail = 12;

        while (secondsInBottom > 0) // we add secondsInBotom grains of sand following the rule
        {
            if (trail < 22)         // trail of falling sand
            {
                hourglass[trail] = addSand(hourglass[trail]);
                trail++;
            }
            else                    // sand on the bottom
            {
                index = getIndexOfNextLineSandIsSupposedToFallOn("BOTTOM",hourglass);
                hourglass[index] = addSand(hourglass[index]);
            }
            secondsInBottom--;
        }

        // Printing the new hourglass

        for (int i = 0; i < 23; i++)
            if (i < 22)
                Console.WriteLine(hourglass[i]);
            else
                Console.Write(hourglass[i]);
    }

    private static List<string> resetHourglass(List<string> hourglass)
    {
        for (int i = 1; i < hourglass.Count-1; i++)
        {
            if (i > 11)
                hourglass[i] = hourglass[i].Replace("o", " ");
            else
            {
                int spaces = 0;
                while (hourglass[i][0] == ' ')
                {
                    hourglass[i] = hourglass[i].Remove(0, 1);
                    spaces++;
                }
                hourglass[i] = hourglass[i].Replace(' ', 'o');
                for (int k = 0; k < spaces; k++)    // we reapply the removed spaces
                    hourglass[i] = hourglass[i].Insert(0, " ");
            }
        }
        return hourglass;
    }

    private static int getSandAmount(string half, List<string> hourglass)
    {
        int sand = 0;
        switch(half)
        {
            case "TOP":
                for (int i = 1; i <= 10; i++)
                    sand += getSandAmountOnLine(hourglass[i]);
                break;

            case "BOTTOM":
                for (int i = 12; i <= 21; i++)
                    sand += getSandAmountOnLine(hourglass[i]);
                break;
        }
        return sand;
    }

    private static int getIndexOfNextLineSandIsSupposedToFallOn(string half, List<string> hourglass) // I couldn't think of a better name
    {
        switch (half)
        {
            case "TOP":
                for (int i = 1; i <= 10; i++)
                    if (!isFull(hourglass[i]) && !isEmpty(hourglass[i]) && getSpaceAmountOnLine(hourglass[i])% 4 != 1)
                        return i;

                int prevSpace = getSpaceAmountOnLine(hourglass[1]);
                for (int i = 1; i < 10;i++)
                {
                    int space = getSpaceAmountOnLine(hourglass[i]);

                    if (prevSpace - space > 4)
                        return i;

                    prevSpace = space;
                }

                for (int i = 1; i <= 10; i++)
                    if (!isEmpty(hourglass[i]))
                        return i;

                break;

            case "BOTTOM":
                for (int i = 21; i >= 12; i--)
                    if (getSandAmountOnLine(hourglass[i]) % 2 == 0)
                        return i;

                int prevSand = getSandAmountOnLine(hourglass[21]);
                for (int i = 20; i >= 12; i--)
                {
                    int sand = getSandAmountOnLine(hourglass[i]);

                    if (prevSand - sand > 2)
                        return i;

                    prevSand = sand;
                }

                return 21;
        }
        return -1;
    }

    private static int getSandAmountOnLine(string line)
    {
        int sand = 0;
        foreach (char c in line)
            if (c == 'o')
                sand++;
        return sand;
    }

    private static int getSpaceAmountOnLine(string line)
    {
        while (line[0] == ' ')
            line = line.Remove(0, 1);

        int space = 0;
        foreach (char c in line)
            if (c == ' ')
                space++;
        return space;

    }
    private static string removeSand(string line)
    {
        int spaces = 0;

        while (line[0] == ' ')  // we transform "   \ooooo     ooooo/" into "\ooooo     ooooo/"
        {
            line = line.Remove(0, 1);
            spaces++;
        }

        int index = line.Length / 2, direction = 1, k = 2;  // 1 right, -1 left

        while (line[index] == ' ')   // we search for the index where the next grain of sand should be added on this line
        {
            index += direction;
            if (direction > 0)
                direction = -k;
            else
                direction = k;
            k++;
        }

        StringBuilder sb = new StringBuilder(line);
        sb[index] = ' ';    // we remove the grain of sand

        for (int i = 0; i < spaces; i++)    // we reapply the removed spaces
            sb.Insert(0, ' ');

        return sb.ToString();
    }

    private static string addSand(string line)
    {
        int spaces = 0;

        while (line[0] == ' ')  // we transform "  /        oo       \" into "/        oo       \"
        {
            line = line.Remove(0, 1);
            spaces++;
        }

        int index = line.Length / 2, direction =1,k=2;  // 1 right, -1 left

        while(line[index] == 'o')   // we search for the index where the next grain of sand should be added on this line
        {
            index += direction;
            if (direction > 0)
                direction = -k;
            else
                direction = k;
            k++;
        }

        StringBuilder sb = new StringBuilder(line);
        sb[index] = 'o';    // we add the grain of sand

        for (int i = 0; i < spaces; i++)    // we reapply the removed spaces
            sb.Insert(0, ' ');

        return sb.ToString();  
    }

    private static bool isFull(string line)
    {
        while (line[0] == ' ')
            line = line.Remove(0, 1);

        foreach (char c in line)
            if (c == ' ')
                return false;

        return true;
    }

    private static bool isEmpty(string line)
    {
        foreach (char c in line)
            if (c == 'o')
                return false;

        return true;
    }
}
