import java.util.*;

public class Solution {

	public static void main(String[] args) {
		Scanner in = new Scanner(System.in);
		int N = in.nextInt();
		in.nextLine();
		for (int i = 0; i < N; i++) {
			String str = in.nextLine();
			String[] ss = str.split("\\s");
			int n = Integer.parseInt(ss[0]);
			// a box to keep n cylinders
			Box b = new Box(n);
			for (int j = 0; j < n; j++) {
				int r = Integer.parseInt(ss[j + 1]);
				b.addRadius(r);
			}
			b.ans();
		}
		in.close();
	}
}

class Box {
	static boolean isDebug = false;

	int n;
	List<Integer> rList; // list of radius

	public Box(int n) {
		this.n = n;
		rList = new ArrayList<>();
	}

	public void ans() {
		List<List<Integer>> llist = Perm.listPermutations(rList);

		double min = Double.MAX_VALUE;

		for (List<Integer> rList : llist) {
			List<Circle> cList = new ArrayList<>();
			double dist = calc(rList, cList);

			if (dist < min) {
				min = dist;
			}
		}
		System.out.printf("%.3f\n", min);
	}

	private double calc(List<Integer> rList, List<Circle> cList) {
		for (int r : rList) {
			cList.add(new Circle(r));
		}

		Circle c0 = cList.get(0);
		c0.setCenterAt(c0.r);

		double maxCenter2 = 0.0;

		for (int i = 1; i < n; i++) {
			Circle c2 = cList.get(i);
			for (int j = i - 1; j >= 0; j--) {
				Circle c1 = cList.get(j);
				double hori = horiDistanceBetweenCenters(c1, c2);
				double center2 = c1.getCenterAt() + hori;

				if (center2 > maxCenter2) {
					maxCenter2 = center2;
				}
			}

			if (maxCenter2 < c2.r) {
				c2.setCenterAt(c2.r);
			} else {
				c2.setCenterAt(maxCenter2);
			}
		} // for

		double max = Double.MIN_VALUE;
		for (Circle cx : cList) {
			double rhs = cx.getRHS();
			if (rhs > max) {
				max = rhs;
			}
		}
		return max;
	}

	private double horiDistanceBetweenCenters(Circle c1, Circle c2) {
		int r1 = c1.r;
		int r2 = c2.r;
		double hori = 2.0 * Math.sqrt(r1 * r2);
		return hori;
	}

	public void addRadius(int r) {
		rList.add(r);
	}
}

class Circle {
	int r;
	double centerAt;
	double rhs;

	public Circle(int r) {
		this.r = r;
	}

	public double getRHS() {
		this.rhs = this.centerAt + this.r;
		return rhs;
	}

	public double getCenterAt() {
		return centerAt;
	}

	public void setCenterAt(double centerAt) {
		this.centerAt = centerAt;
	}
}

class Perm {
	public static List<List<Integer>> listPermutations(List<Integer> list) {
		if (list.size() == 0) {
			List<List<Integer>> result = new ArrayList<List<Integer>>();
			result.add(new ArrayList<Integer>());
			return result;
		}
		List<List<Integer>> returnMe = new ArrayList<List<Integer>>();
		Integer firstElement = list.remove(0);
		List<List<Integer>> recursiveReturn = listPermutations(list);
		for (List<Integer> li : recursiveReturn) {
			for (int index = 0; index <= li.size(); index++) {
				List<Integer> temp = new ArrayList<>(li);
				temp.add(index, firstElement);
				returnMe.add(temp);
			}
		}
		return returnMe;
	}
}
