using System;
using System.Linq;
using System.IO;
using System.Text;
using System.Collections;
using System.Collections.Generic;

class Solution {
    static void Main(string[] args) {
        string[] inputs = Console.ReadLine().Split(' ');
        int E = int.Parse(inputs[0]);
        int L = int.Parse(inputs[1]);
        int N = int.Parse(inputs[2]);
        string start = Console.ReadLine();

        var line = start.Split(' ').ToList();

        var fromEnd = L + E - N;
        for (var i = 1; i < L; i++) {
            if (N < line.Count) line = line.Take(N).ToList();
            else if (fromEnd < line.Count) line = line.Skip(line.Count - fromEnd).ToList();
            var next = new List<string>();
            var prevEl = string.Empty;
            for (var j = 0; j < line.Count; j++) {
                next.Add(Combine(prevEl, line[j]));
                prevEl = line[j];
            }
            next.Add(line.Last());
            line = next;
        }

        if (N <= line.Count) Console.WriteLine(line[N-1]);
        else Console.WriteLine(line[line.Count-fromEnd]);
    }

    private static string Combine(string a, string b) {
        long lA;
        long lB;
        if (long.TryParse(a, out lA) && long.TryParse(b, out lB)) return (lA + lB).ToString();
        return a + b;
    }
}
