float convToBytes(float size, char *unit) {
  char tab[5] = {'B', 'K', 'M', 'G', 'T'};
  for (int i = 0; i < 5 && tab[i] != unit[0]; i++)
    size *= 1024;
  return size;
}

void printSize(float size) {
  char *tab[5] = {"B", "KiB", "MiB", "GiB", "TiB"};
  int i;
  for (i = 0; size / 1024 > 1; i++)
    size /= 1024;

  if ((int)size == size)
    printf("%d %s", (int)size, tab[i]);
  else
    printf("%.1f %s", size, tab[i]);
}

int main() {
  float disk_size;
  char disk_unit[3];
  scanf("%f%s", &disk_size, disk_unit);

  float file_size;
  char file_unit[3];
  scanf("%f%s", &file_size, file_unit);

  float dsl = convToBytes(disk_size, disk_unit);
  float fs = convToBytes(file_size, file_unit);
  float l = dsl - fs;

  if (l < 0)
    printf("Not enough space\n");
  else
    printSize(l);

  return 0;
}
