import java.util.Scanner;

public class Solution {

	public static void main(String[] args) {
		Scanner in = new Scanner(System.in);
		int n = in.nextInt();
		for (int i = 0; i < n; i++) {
			int row = in.nextInt();
			int col = in.nextInt();
			int w = in.nextInt();
			process(row, col, w);
		}
		in.close();
	}

	private static void process(int row, int col, int w) {
		boolean isWhite = (w == 0) ? false : true;

		boolean bothCornersBlack = false;
		if (!isWhite && (row % 2 == 0 || col % 2 == 0)) {
			bothCornersBlack = true;
		}

		int c1 = -1;
		int c2 = -1;
		if (!bothCornersBlack) {
			c1 = (col - 6) / 2; // odd
			c2 = (col - 7) / 2; // even
		}
		// both are black
		else {
			c1 = (col - 7) / 2; // odd
			c2 = (col - 6) / 2; // even
		}

		int usable = row - 7;
		int oddCount = (usable + 1) / 2;
		int evenCount = usable / 2;

		int ans = oddCount * c1 + evenCount * c2;
		System.out.println(ans);
	}
}
