#include <iostream>
using namespace std;
int main()
{
    int n,t=65;
    cin>>n;
    for(int i=1;i<=n;i++)
    {
        for(int j=1;j<=n-i;j++)
        {
            cout<<" ";
        }
        for(int k=1;k<=i;k++)
        {
            if(k==i)
                cout<<(char)t++;
            else
                cout<<(char)t++<<" ";
            if(t==91)
                t=65;
        }
        cout<<"\n";
    }
    return 0;
}
