using System;

class Tumbler
{
    static void Main(string[] args)
    {
        int n = int.Parse(Console.ReadLine());
        string x = Console.ReadLine();

        string[] z = x.Split(' ');
        int last = int.Parse(z[0]);

        for(int i=1;i<n;i++) {
            int pin = int.Parse(z[i]);

            if(last%pin==0) {
                Console.Write('/');
                last/=pin;
            } else if(last%2==pin%2) {
                if(last>pin) {
                    Console.Write('-');
                    last-=pin;
                } else {
                    Console.Write('+');
                    last+=pin;
                }

            } else {
                if(last>pin){
                    Console.Write('-');
                    last-=pin;
                } else {
                    Console.Write('*');
                    last*=pin;
                }
            }
        }


        Console.Write(" "+last);


    }
}
