#include <iostream>
#include <vector>

using namespace std;

const int MAX_SIZE = 1e5;

int main() {


	string in;
	getline(cin, in);

	int p = 0;
	char m[MAX_SIZE];
	bool esc = false;

	for (int i = 0; i < (int) in.length(); i++) {
		if (in[i] == '<' && !esc) {
			if (p > 0)
				--p;
			continue;
		} else if (in[i] == '\\') {
			if (i+1 >= (int) in.length() || in[i+1] != '<') {
				m[p++] = '\\';
			} else {
				esc = true;
			}
			continue;
		}
		m[p++] = in[i];
		if (esc) {
			esc = false;
		}
	}

	for (int i = 0; i < p; i++) {
		cout << m[i];
	}
	cout << endl;

	return 0;
}
