using System;

class Solution
{
    static void Main(string[] args)
    {
        int level = int.Parse(Console.ReadLine());
        string[] coords = Console.ReadLine().Split();
        long x1 = long.Parse(coords[0]);
        long y1 = long.Parse(coords[1]);
        long x2 = long.Parse(coords[2]);
        long y2 = long.Parse(coords[3]);

        for (long y = y1; y <= y2; y++)
        {
            for (long x = x1; x <= x2; x++)
            {
                Console.Write((isThreadOne(x, y) ? '0' : '+'));
            }
            Console.WriteLine();
        }
    }

    static bool isThreadOne(long x, long y)
    {
        while (x > 0 || y > 0)
        {
            if (x % 3 == 1 && y % 3 == 1)
                return false;
            x /= 3;
            y /= 3;
        }
        return true;
    }
}
