#include <bits/stdc++.h>
using namespace std;

int main(){

    int n;
    cin >> n;

    vector<long long> areas(n);
    for(int i = 0; i < n; i++){
        cin >> areas[i];
        areas[i] *= areas[i];
    }

    sort(areas.begin(), areas.end());

    long long ans = (areas[0] * 4);

    for (int i = 1; i < n - 1; i++){
        ans += areas[i] * 5 - areas[i - 1];
    }

    if (n > 1){
        ans += ((areas[n - 1] * 6) - areas[n - 2]);
    }
    else{
        ans += areas[0];
    }

    cout << ans << endl;
}
