#include <stdlib.h>
#include <stdio.h>
#include <string.h>
#include <stdbool.h>
#include <math.h>

/**
 * Hints:
 * dx = sin(Angle) * Distance
 * dy = cos(Angle) * Distance
 * Angle = atan( x / y )
 **/

int main()
{
    // Number of friend to Juliet's house
    int N;
    scanf("%d", &N);
    float Julietx = 0;
    float Juliety = 0;
    for (int i = 0; i < N; i++) {
        // Heading (angle form north) between this friend and the last one
        int H;
        // Distance between this friend and the last one
        int D;
        scanf("%d%d", &H, &D);
        float RadAngl = H * M_PI / 180;
        Julietx += sinf(RadAngl) * D;
        Juliety += cosf(RadAngl) * D;
    }

    float ResultH = atanf(Julietx/Juliety) * 180.0 / M_PI;
    float ResultD = sqrt(pow(Julietx,2)+pow(Juliety,2));
    printf("%d %d\n",(int) roundf(ResultH),(int) roundf(ResultD));

    return 0;
}
