import java.util.*;
import java.io.*;
import java.math.*;

class Solution {

    public static void main(String args[]) {
        Scanner in = new Scanner(System.in);
        int h = in.nextInt();
        int w = in.nextInt();
        int n = in.nextInt();
        if (in.hasNextLine()) {
            in.nextLine();
        }
        List<Passenger>[]plane = new ArrayList[h];
        for(int i = 0; i < h; i++) {
            List<Passenger> row=new ArrayList<>();
            plane[i] = row;
        }
        for (int i = 0; i < n; i++) {
            String passenger = in.nextLine();
            Passenger p = new Passenger(passenger);
            plane[p.row-1].add(p);
        }

        for(int i = 0; i < h; i++) {
            Collections.sort(plane[i],(Passenger a, Passenger b) -> a.col-b.col);
        }
        int boarded=0;
        while(boarded<n){
            String bGroup = "";
            // board the next person on the left half of each row
            for(int r = h ; r > 0; r--){
                if(!plane[r-1].isEmpty()){
                    Passenger p = plane[r-1].get(0);
                    if(p.col<=w/2){
                        boarded++;
                        bGroup = bGroup + (bGroup.isEmpty() ? "" : ",") + p.name;
                        plane[r-1].remove(p);
                    }
                }
            }
            if(!bGroup.isEmpty()){
                System.out.println("Now boarding: " + bGroup);
            }

            // board the next person on the right half of each row
            bGroup = "";
            for(int r = h ; r > 0; r--){
                if(!plane[r-1].isEmpty()){
                    Passenger p = plane[r-1].get(plane[r-1].size() - 1);
                    if(p.col>w/2){
                        boarded++;
                        bGroup = bGroup + (bGroup.isEmpty() ? "" : ",") + p.name;
                        plane[r-1].remove(p);
                    }
                }
            }
            if(!bGroup.isEmpty()){
                System.out.println("Now boarding: " + bGroup);
            }
        }
    }
}

class Passenger{
    String name;
    String seat;
    int row;
    int col;
    public Passenger(String passenger){
        String[]details = passenger.split(",");
        name = details[0];
        seat = details[1];
        row=Integer.parseInt(seat.replaceAll("\\D",""));
        col=seat.charAt(seat.length()-1)-'A'+1;
    }
}
