decoder_dict = {}
num_swaps = int(input())
for _ in range(num_swaps):
    a, b = input().split(' -> ')
    decoder_dict[a] = b
length = input()
encoded_message = input().split('+')

decoded_message = ''
stack = []
for c in encoded_message:
    if c[0] == '#':
        if len(c) > 1:
            for _ in range(int(c[1:])):
                stack.pop()
    elif c == '%':
        new_stack = [stack[i] for i in range(0, len(stack), 2)]
        new_stack.extend([stack[i] for i in range(1, len(stack), 2)])
        stack = new_stack
    elif c == '*':
        item = stack.pop()
        if item in decoder_dict:
            item = decoder_dict[item]
        decoded_message = decoded_message + item
    else:
        stack.append(c)

formatted_message = []
decoded_message = decoded_message.split()
line = ''
for word in decoded_message:
    if len(line) + len(word) + 1 <= 75:
        if line:
            line = line + ' ' + word
        else:
            line = word
    else:
        formatted_message.append(line)
        line = word

formatted_message.append(line)

print('\n'.join(formatted_message))
