using System; using System.Linq; using System.IO; using System.Text; using System.Collections; using System.Collections.Generic;

public class Program
{
	public static char[,] cRay;//do it like reading, first lin, then pos
	public static int outerPlusHiWi;
	public static int innerPlusHiWi;
	public static int thickness;
	public static LogoModel TheLogo;
	public static int RayLinTotal;
	public static int RayPosTotal;

	public static void Main()
	{

        int size = int.Parse(Console.ReadLine());
        outerPlusHiWi = size;

        thickness = int.Parse(Console.ReadLine());

        int N = int.Parse(Console.ReadLine());

        var inputList = new List<string>();
        for (int i = 0; i < N; i++)
        {
            string logoLine = Console.ReadLine();
            inputList.Add(logoLine);
        }

		innerPlusHiWi = outerPlusHiWi - 2;

		TheLogo = new LogoModel();

		int longestInputLine = 0;
		foreach (var item in inputList)
		{
			if (item.Length > longestInputLine) longestInputLine = item.Length;
		}

		for (int i = 0 ; i < inputList.Count() ; i++)
		{
			if (inputList[i].Length < longestInputLine)
			{
				string padding = new string(' ', longestInputLine - inputList[i].Length);
				inputList[i] = inputList[i] + padding;
			}
		}

		RayPosTotal = outerPlusHiWi * longestInputLine ; 
		RayLinTotal = outerPlusHiWi * inputList.Count()  ;
		cRay = new char[RayLinTotal, RayPosTotal];//do it like reading, first you care about lin, then about pos in lin
		InitcRay();

		for (int inputLin = 0 ; inputLin <  inputList.Count() ; inputLin++) //this way because like reading, line after line
		{
			string thisinputLin = inputList[inputLin];
			for (int inputPos = 0 ; inputPos < longestInputLine; inputPos++)
			{
				if (thisinputLin[inputPos] == '+')
				{
					PlusModel plus = new PlusModel(inputLin * outerPlusHiWi, inputPos * outerPlusHiWi);
					plus.CarveIt();
					TheLogo.AddAPlus(plus);
				}	
			}
		}
		TheLogo.SurroundThem();
		TheLogo.RemoveBorders();
		PrintcRay();
	}

	public class PlusModel
	{
		public int centerLin ;
		public int centerPos ;
		public int topLin;
		public int topPos;
		public int a, b, c, d;      //top left and bottom right of vertical   part of the plus-sign 
		public int aa, bb, cc, dd;  //top left and bottom right of horizontal part of the plus-sign 

		public PlusModel( int topLin, int topPos)
		{
			this.topLin = topLin;
			this.topPos = topPos;
			int halfSize = (outerPlusHiWi - thickness) / 2;
			int innerThickness = thickness - 2;

			a = topLin + 1;
			b = topPos + halfSize + 1;
			c = a + innerPlusHiWi - 1;
			d = b + innerThickness - 1;

			aa = topLin + halfSize + 1;
			bb = topPos + 1;
			cc = aa + innerThickness - 1;
			dd = bb + innerPlusHiWi - 1;

		}
		public void CarveIt()
		{
			DrawInRay(a,   b,   c,   d, 'e');//"e" for end part.  Then will check if there are 2 ends close together and if so remove their border
			DrawInRay(a+1, b,   c-1, d, 's');//"s" for save.  (aka: Dont put + here)

            DrawInRay(aa,  bb,     cc,  dd,   'e');
			DrawInRay(aa,  bb+1,   cc,  dd-1, 's');
		}
	}

	public static void DrawInRay(int lin1, int pos1, int lin2, int pos2, char symbol)
	{
		for (int lin = lin1 ; lin <= lin2 ; lin++)
		{
			for (int pos = pos1 ; pos <= pos2 ; pos++)
			{
				cRay[lin,pos] = symbol;
			}
		}
	}

	public class LogoModel
	{
		public List<PlusModel> PlusList;
		public LogoModel()
		{
			PlusList= new List<PlusModel>();
		}

		public void AddAPlus(PlusModel aPlus)
		{
			PlusList.Add(aPlus);
		}

		public void RemoveBorders()
		{
			for (int lin = 0; lin < RayLinTotal; lin++)
        	{
				string thisRow = "";
            	for (int pos = 0; pos < RayPosTotal; pos++)
            	{
					char ch = cRay[lin, pos];
			        if (ch =='e' && pos + 3 < RayPosTotal)// There is enough room to the right
					{
						bool fitsPatternOnLin = cRay[lin, pos+1] == '+' &&
												cRay[lin, pos+2] == '+' &&
												cRay[lin, pos+3] == 'e';
						if (fitsPatternOnLin) 
						{
							cRay[lin, pos+1] = 's' ;
							cRay[lin, pos+2] = 's' ;
						}
					}

					if (ch =='e' && lin + 3 < RayLinTotal)// There is enough room going down
					{
						bool fitsPatternOnPos = cRay[lin+1, pos] == '+' &&
												cRay[lin+2, pos] == '+' &&
												cRay[lin+3, pos] == 'e';
						if (fitsPatternOnPos) 
						{
							cRay[lin+1, pos] = 's' ;
							cRay[lin+2, pos] = 's' ;
						}	
					}		
				}
			}
		}

		public void SurroundThem()
		{
			for (int LIN = 0; LIN < RayLinTotal; LIN++)
			{
				for (int POS = 0; POS < RayPosTotal; POS++)
				{
					char ch = cRay[LIN,POS];
					if (ch == 's' || ch == 'e')
					{
						for (int lin = 0; lin < RayLinTotal; lin++)
						{
							for (int pos = 0; pos < RayPosTotal; pos++)
							{
								bool b1 = Math.Abs(POS-pos) <= 1;
								bool b2 = Math.Abs(LIN-lin) <= 1;
								bool b3 = b1 && b2;                                    // if its close by 
								bool b4 = cRay[lin,pos] != 's' && cRay[lin,pos] != 'e';//if its not saved or end

								if (b3 && b4)
								{
									cRay[lin,pos] = '+';
								}
							}
						}
					}
				}	
			}
		}
	}

	public static void PrintcRay()
    {
		int numChars = 0;
        for (int lin = 0; lin < RayLinTotal; lin++)
        {
			string thisLin = "";
            for (int pos = 0; pos < RayPosTotal; pos++)
            {
				char ch = cRay[lin, pos];
                if (ch == 's' || ch == 'e' || ch == '.')
                {
                    thisLin = thisLin + ' ';
                }
				else
				{
                    thisLin = thisLin + ch;
				}
            }
            Console.WriteLine(thisLin.TrimEnd());
        }
    }

	public static void InitcRay()
    {
        for (int lin = 0; lin < RayLinTotal; lin++)
        {
            for (int pos = 0; pos < RayPosTotal; pos++)
            {
                cRay[lin,pos] = '.';
            }
        }
    }
}
